import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import api from "../lib/api"; // same api helper you use in AdminDashboard

export default function ApplicationDetails() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [loading, setLoading] = useState(true);
  const [app, setApp] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        setLoading(true);
        setError("");

        // adjust endpoint name to match your backend
        const res = await api.get(`/admin/applications/${id}`);
        setApp(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load application details.");
        toast.error("Failed to load application.");
      } finally {
        setLoading(false);
      }
    };

    fetchDetails();
  }, [id]);

  const goBack = () => navigate(-1);

  const statusColor = (status) => {
    switch (status) {
      case "approved":
        return { background: "#DCFCE7", color: "#166534" };
      case "rejected":
        return { background: "#FEE2E2", color: "#991B1B" };
      default:
        return { background: "#FEF9C3", color: "#854D0E" }; // pending
    }
  };

  if (loading) {
    return (
      <div className="app-details-page">
        <div className="app-details-card">Loading...</div>
      </div>
    );
  }

  if (error || !app) {
    return (
      <div className="app-details-page">
        <div className="app-details-card">
          <p>{error || "Application not found."}</p>
          <button className="btn" onClick={goBack}>Back</button>
        </div>
      </div>
    );
  }

  const {
    title,
    company_name,
    company_location,
    company_website,
    student_name,
    student_email,
    student_university,
    student_major,
    status,
    applied_at,
    notes,
    internship_location,
    internship_start_date,
    internship_duration,
    internship_mode,
    internship_salary,
    internship_description,
  } = app;

  return (
    <div className="app-details-page">
      <div className="app-details-header">
        <button className="btn btn--ghost" onClick={goBack}>← Back</button>
        <h1>Application Details</h1>
      </div>

      <div className="app-details-card">
        {/* Top summary row */}
        <div className="app-summary">
          <div>
            <h2>{title}</h2>
            <p className="muted">
              {company_name} • {internship_location || company_location || "—"}
            </p>
          </div>
          <div className="status-badge" style={statusColor(status)}>
            {status}
          </div>
        </div>

        {/* Two-column layout */}
        <div className="app-grid">
          {/* Internship info */}
          <section>
            <h3>Internship Info</h3>
            <div className="info-list">
              <InfoRow label="Company" value={company_name} />
              <InfoRow label="Location" value={internship_location || company_location} />
              <InfoRow label="Work Mode" value={internship_mode} />
              <InfoRow label="Duration" value={internship_duration && `${internship_duration} months`} />
              <InfoRow label="Start Date" value={formatDate(internship_start_date)} />
              <InfoRow label="Salary" value={internship_salary ? `AED ${internship_salary}` : "Unpaid / Not specified"} />
              <InfoRow
                label="Company Website"
                value={
                  company_website ? (
                    <a href={company_website} target="_blank" rel="noreferrer">
                      {company_website}
                    </a>
                  ) : "—"
                }
              />
            </div>
          </section>

          {/* Student info */}
          <section>
            <h3>Student Info</h3>
            <div className="info-list">
              <InfoRow label="Name" value={student_name} />
              <InfoRow label="Email" value={student_email} />
              <InfoRow label="University" value={student_university} />
              <InfoRow label="Major" value={student_major} />
              <InfoRow label="Applied At" value={formatDateTime(applied_at)} />
            </div>
          </section>
        </div>

        {/* Description / Notes */}
        <section className="app-notes">
          <h3>Internship Description</h3>
          <p>{internship_description || "No description provided."}</p>

          <h3 style={{ marginTop: "1.5rem" }}>Admin / Student Notes</h3>
          <p>{notes || "No additional notes."}</p>
        </section>
      </div>
    </div>
  );
}

function InfoRow({ label, value }) {
  return (
    <div className="info-row">
      <span className="info-label">{label}</span>
      <span className="info-value">{value || "—"}</span>
    </div>
  );
}

function formatDate(dateStr) {
  if (!dateStr) return "—";
  const d = new Date(dateStr);
  if (Number.isNaN(d.getTime())) return dateStr;
  return d.toLocaleDateString();
}

function formatDateTime(dateStr) {
  if (!dateStr) return "—";
  const d = new Date(dateStr);
  if (Number.isNaN(d.getTime())) return dateStr;
  return d.toLocaleString();
}
