// src/pages/AdminDirectory.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import api from "../lib/api";

export default function AdminDirectory() {
  const [students, setStudents] = useState([]);
  const [employers, setEmployers] = useState([]);
  const [internships, setInternships] = useState([]);
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);

  // filters
  const [userStatusFilter, setUserStatusFilter] = useState("all");
  const [internshipStatusFilter, setInternshipStatusFilter] = useState("all");
  const [applicationStatusFilter, setApplicationStatusFilter] =
    useState("all");

  const navigate = useNavigate();

  useEffect(() => {
    const loadAll = async () => {
      try {
        setLoading(true);

        const [usersRes, internshipsRes, applicationsRes] = await Promise.all([
          api.get("/api/admin/users"),
          api.get("/api/admin/pending-internships"),
          api.get("/api/admin/pending-applications"),
        ]);

        const users = usersRes.data || usersRes;
        setStudents(users.filter((u) => u.role === "student"));
        setEmployers(users.filter((u) => u.role === "employer"));

        setInternships(internshipsRes.data || internshipsRes);
        setApplications(applicationsRes.data || applicationsRes);
      } catch (err) {
        console.error(err);
        toast.error("Failed to load directory data");
      } finally {
        setLoading(false);
      }
    };

    loadAll();
  }, []);

  // ---------- helpers ----------
  const badgeStyle = (status) => {
    if (!status) return {};
    switch (status.toLowerCase()) {
      case "approved":
        return { color: "#16a34a", fontWeight: 600 };
      case "rejected":
        return { color: "#dc2626", fontWeight: 600 };
      default:
        return { color: "#ca8a04", fontWeight: 600 };
    }
  };

  const formatDate = (d) => {
    if (!d) return "—";
    const dt = new Date(d);
    if (Number.isNaN(dt.getTime())) return d;
    return dt.toLocaleDateString();
  };

  // ---------- USER ACTIONS ----------
  const approveUser = async (id, name) => {
    if (!window.confirm(`Approve user "${name}"?`)) return;
    try {
      await api.post(`/api/admin/users/${id}/approve`);
      setStudents((list) =>
        list.map((u) => (u.id === id ? { ...u, status: "approved" } : u))
      );
      setEmployers((list) =>
        list.map((u) => (u.id === id ? { ...u, status: "approved" } : u))
      );
      toast.success("User approved");
    } catch (e) {
      console.error(e);
      toast.error("Failed to approve user");
    }
  };

  const rejectUser = async (id, name) => {
    if (!window.confirm(`Reject user "${name}"?`)) return;
    try {
      await api.post(`/api/admin/users/${id}/reject`);
      setStudents((list) =>
        list.map((u) => (u.id === id ? { ...u, status: "rejected" } : u))
      );
      setEmployers((list) =>
        list.map((u) => (u.id === id ? { ...u, status: "rejected" } : u))
      );
      toast.success("User rejected");
    } catch (e) {
      console.error(e);
      toast.error("Failed to reject user");
    }
  };

  // ---------- INTERNSHIP ACTIONS ----------
  const approveInternship = async (id, title) => {
    if (!window.confirm(`Approve internship "${title}"?`)) return;
    try {
      await api.post(`/api/admin/internships/${id}/approve`);
      setInternships((list) =>
        list.map((i) => (i.id === id ? { ...i, status: "approved" } : i))
      );
      toast.success("Internship approved");
    } catch (e) {
      console.error(e);
      toast.error("Failed");
    }
  };

  const rejectInternship = async (id, title) => {
    if (!window.confirm(`Reject internship "${title}"?`)) return;
    const reason = window.prompt("Reason (optional):", "");
    try {
      await api.post(`/api/admin/internships/${id}/reject`, { reason });
      setInternships((list) =>
        list.map((i) => (i.id === id ? { ...i, status: "rejected" } : i))
      );
      toast.success("Internship rejected");
    } catch (e) {
      console.error(e);
      toast.error("Failed");
    }
  };

  // ---------- APPLICATION ACTIONS ----------
  const approveApplication = async (id, title) => {
    if (!window.confirm(`Approve application for "${title}"?`)) return;
    try {
      await api.post(`/api/admin/applications/${id}/approve`);
      setApplications((list) =>
        list.map((a) => (a.id === id ? { ...a, status: "approved" } : a))
      );
      toast.success("Application approved");
    } catch (e) {
      console.error(e);
      toast.error("Failed");
    }
  };

  const rejectApplication = async (id, title) => {
    if (!window.confirm(`Reject application for "${title}"?`)) return;
    const reason = window.prompt("Reason (optional):", "");
    try {
      await api.post(`/api/admin/applications/${id}/reject`, { reason });
      setApplications((list) =>
        list.map((a) => (a.id === id ? { ...a, status: "rejected" } : a))
      );
      toast.success("Application rejected");
    } catch (e) {
      console.error(e);
      toast.error("Failed");
    }
  };

  // ---------- FILTERED LISTS ----------
  const allUsers = [...students, ...employers];

  const filteredUsers = allUsers.filter((u) =>
    userStatusFilter === "all"
      ? true
      : (u.status || "pending").toLowerCase() === userStatusFilter
  );

  const filteredInternships = internships.filter((i) =>
    internshipStatusFilter === "all"
      ? true
      : (i.status || "pending").toLowerCase() === internshipStatusFilter
  );

  const filteredApplications = applications.filter((a) =>
    applicationStatusFilter === "all"
      ? true
      : (a.status || "pending").toLowerCase() === applicationStatusFilter
  );

  return (
    <div className="admin-dir">
      <style>{`
       .admin-dir{
  width:100%;
  min-height:calc(100vh - 120px);
  padding:32px 0 40px;
  font-family:system-ui,-apple-system,"Segoe UI",sans-serif;
}


        .admin-dir-inner{
          width:100%;
          max-width:1150px;
          padding:0 24px;
        }

        h1{
          font-size:26px;
          font-weight:700;
          margin-bottom:20px;
        }

        .grid-3{
          display:grid;
          gap:20px;
          grid-template-columns:1fr;
        }

        @media(min-width:1024px){
          .grid-3{
            grid-template-columns:repeat(3,1fr);
          }
        }

        .card{
          background:#fff;
          border-radius:16px;
          padding:14px;
          box-shadow:0 12px 35px rgba(15,23,42,.06);
          height:100%;
        }

        .card-header{
          display:flex;
          justify-content:space-between;
          align-items:center;
          font-weight:700;
          margin-bottom:10px;
        }

        table{
          width:100%;
          border-collapse:collapse;
          font-size:13px;
        }
        th,td{
          padding:8px 10px;
          text-align:left;
        }
        thead{
          background:#0f2b63;
          color:#fff;
        }
        tbody tr:nth-child(even){background:#f9fafb;}
        tbody tr:nth-child(odd){background:#fff;}

        .btn-view{
          padding:4px 10px;
          border-radius:999px;
          border:1px solid #e5e7eb;
          background:#f9fafb;
          cursor:pointer;
          font-size:12px;
        }
        .btn-approve{
          padding:4px 10px;
          background:#16a34a;
          color:#fff;
          border:none;
          border-radius:999px;
          cursor:pointer;
          font-size:12px;
        }
        .btn-reject{
          padding:4px 10px;
          background:#dc2626;
          color:#fff;
          border:none;
          border-radius:999px;
          cursor:pointer;
          font-size:12px;
        }
        .actions{
          display:flex;
          gap:6px;
        }

        .filter-select{
          font-size:12px;
          padding:4px 8px;
          border-radius:999px;
          border:1px solid #d1d5db;
          background:#f9fafb;
          outline:none;
        }
      `}</style>

      <div className="admin-dir-inner">
        <h1>Platform Directory</h1>

        {loading ? (
          <p>Loading...</p>
        ) : (
          <div className="grid-3">
            {/* USERS */}
            <div className="card">
              <div className="card-header">
                <span>Users (Students & Employers)</span>
                <select
                  className="filter-select"
                  value={userStatusFilter}
                  onChange={(e) => setUserStatusFilter(e.target.value)}
                >
                  <option value="all">All</option>
                  <option value="pending">Pending</option>
                  <option value="approved">Approved</option>
                  <option value="rejected">Rejected</option>
                </select>
              </div>
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Role</th>
                    <th>Status</th>
                    <th>View</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredUsers.map((u) => (
                    <tr key={u.id}>
                      <td>{u.name}</td>
                      <td>{u.role === "student" ? "Student" : "Employer"}</td>
                      <td style={badgeStyle(u.status)}>{u.status}</td>
                      <td>
                        <button
                          className="btn-view"
                          onClick={() => navigate(`/admin/users/${u.id}`)}
                        >
                          View
                        </button>
                      </td>
                      <td>
                        <div className="actions">
                          {!u.status || u.status === "pending" ? (
                            <>
                              <button
                                className="btn-approve"
                                onClick={() => approveUser(u.id, u.name)}
                              >
                                Approve
                              </button>
                              <button
                                className="btn-reject"
                                onClick={() => rejectUser(u.id, u.name)}
                              >
                                Reject
                              </button>
                            </>
                          ) : (
                            <span>—</span>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* INTERNSHIPS */}
            <div className="card">
              <div className="card-header">
                <span>Internships</span>
                <select
                  className="filter-select"
                  value={internshipStatusFilter}
                  onChange={(e) => setInternshipStatusFilter(e.target.value)}
                >
                  <option value="all">All</option>
                  <option value="pending">Pending</option>
                  <option value="approved">Approved</option>
                  <option value="rejected">Rejected</option>
                </select>
              </div>
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Company</th>
                    <th>Status</th>
                    <th>View</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredInternships.map((i) => (
                    <tr key={i.id}>
                      <td>{i.title}</td>
                      <td>{i.company_name}</td>
                      <td style={badgeStyle(i.status)}>{i.status}</td>
                      <td>
                        <button
                          className="btn-view"
                          onClick={() =>
                            navigate(`/admin/internships/${i.id}`)
                          }
                        >
                          View
                        </button>
                      </td>
                      <td>
                        <div className="actions">
                          {i.status === "pending" ? (
                            <>
                              <button
                                className="btn-approve"
                                onClick={() =>
                                  approveInternship(i.id, i.title)
                                }
                              >
                                Approve
                              </button>
                              <button
                                className="btn-reject"
                                onClick={() =>
                                  rejectInternship(i.id, i.title)
                                }
                              >
                                Reject
                              </button>
                            </>
                          ) : (
                            <span>—</span>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* APPLICATIONS */}
            <div className="card">
              <div className="card-header">
                <span>Applications</span>
                <select
                  className="filter-select"
                  value={applicationStatusFilter}
                  onChange={(e) => setApplicationStatusFilter(e.target.value)}
                >
                  <option value="all">All</option>
                  <option value="pending">Pending</option>
                  <option value="approved">Approved</option>
                  <option value="rejected">Rejected</option>
                </select>
              </div>
              <table>
                <thead>
                  <tr>
                    <th>Student</th>
                    <th>Internship</th>
                    <th>Status</th>
                    <th>View</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredApplications.map((a) => (
                    <tr key={a.id}>
                      <td>{a.student_name}</td>
                      <td>{a.title}</td>
                      <td style={badgeStyle(a.status)}>{a.status}</td>
                      <td>
                        <button
                          className="btn-view"
                          onClick={() =>
                            navigate(`/admin/applications/${a.id}`)
                          }
                        >
                          View
                        </button>
                      </td>
                      <td>
                        <div className="actions">
                          {a.status === "pending" ? (
                            <>
                              <button
                                className="btn-approve"
                                onClick={() =>
                                  approveApplication(a.id, a.title)
                                }
                              >
                                Approve
                              </button>
                              <button
                                className="btn-reject"
                                onClick={() =>
                                  rejectApplication(a.id, a.title)
                                }
                              >
                                Reject
                              </button>
                            </>
                          ) : (
                            <span>—</span>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
